	public static int[] switchLights(int[] a) {
		int[] b = new int[a.length];
		boolean change = true;
		for (int i = a.length - 1; i >= 0; i--) {
			if (a[i] == 1) {
				change = !change;
			}
			b[i] = !change ? 1 - a[i] : a[i];
		}
		return b;
	}
